import emailjs from 'emailjs-com'

const Mailer = () =>{

    function sendBooking(e){
        e.preventDefault();

        emailjs.sendForm('service_n10bxqc','template_3wgzsne',e.target,
        'BwsrfgZKtZpi1qduV').then(res=>{
            console.log(res)
        }).catch(err=>{
            console.log(err)
        })

    }
    
    return(
        <section style={{backgroundColor:"#FAFDD6", paddingTop:"15px" }}>
        <div className="container border "
        style={{

                width:'50%',
                margin: 'auto',
                marginTop:"60px",
                backgroundImage:`url('https://images.unsplash.com/photo-1621293954908-907159247fc8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=870&q=80')`,
                backgroundPosition:"center",
                backgroundSize:"cover"}}>
            <h1  style={{ margin: 'auto', textAlign: "center" }}> Booking Form </h1>
            <form className="row" style={{margin:"25px 85px 75px 100px"}} onSubmit={{sendBooking}}>
                <label style={{fontWeight:"600"}}>Name</label>
                <input type='text' name='name' className="form-control"/>
                <label style={{fontWeight:"600"}}>Email</label>
                <input type='eamil' name='user_email' className="form-control"/>
                <label style={{fontWeight:"800"}} >Message</label>
                <textarea name='message' rows='4' className="form-control"/>
                <input type='submit' value='Send' className="form-control btn btn-primary"
                style={{marginTop:"30px" }} />
            </form>
        </div>
        </section>
    );
}


export default Mailer ;
